import React, { useState, useEffect } from 'react';
import axios from '../api/axios';

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('/tasks');
        setTasks(response.data);
      } catch (err) {
        setError('Failed to fetch tasks');
      }
    };
    fetchTasks();
  }, []);

  const handleAddTask = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/tasks', { task });
      setTasks([...tasks, response.data]);
      setTask('');
    } catch (err) {
      setError('Failed to add task');
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`/tasks/${id}`);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (err) {
      setError('Failed to delete task');
    }
  };

  const handleMarkDone = async (id) => {
    try {
      await axios.put(`/tasks/${id}/done`);
      setTasks(tasks.map(task => task.id === id ? { ...task, done: true } : task));
    } catch (err) {
      setError('Failed to mark task as done');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-4xl p-8 space-y-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-bold text-center">Dashboard</h2>
        <form onSubmit={handleAddTask} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">New Task:</label>
            <input
              type="text"
              value={task}
              onChange={(e) => setTask(e.target.value)}
              required
              className="w-full px-3 py-2 border rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-indigo-200"
            />
          </div>
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <button type="submit" className="w-full px-4 py-2 text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
            Add Task
          </button>
        </form>
        <div className="mt-6">
          <h3 className="text-xl font-semibold">Your Tasks</h3>
          <ul className="space-y-4">
            {tasks.map((task) => (
              <li key={task.id} className="flex items-center justify-between p-4 border rounded-lg shadow-sm">
                <span className={task.done ? 'line-through' : ''}>{task.task}</span>
                <div className="space-x-2">
                  {!task.done && (
                    <button
                      onClick={() => handleMarkDone(task.id)}
                      className="px-2 py-1 text-sm text-white bg-green-500 rounded-lg hover:bg-green-600"
                    >
                      Mark as Done
                    </button>
                  )}
                  <button
                    onClick={() => handleDeleteTask(task.id)}
                    className="px-2 py-1 text-sm text-white bg-red-500 rounded-lg hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
